package org.example.entity;import java.util.Scanner;public class Alturas {    public double[] pessoas;    public void numPessoas(int pessoas) {        Scanner sc = new Scanner(System.in);        double mediaPeso = 0;        this.pessoas = new double[pessoas];        for(int i = 0; i < pessoas; i++) {            System.out.printf("Qual o peso da pessoa número %d?%n", i + 1);            this.pessoas[i] = sc.nextDouble();            sc.nextLine();            mediaPeso = (mediaPeso + this.pessoas[i]);        }        sc.close();        mediaPeso = mediaPeso / this.pessoas.length;        System.out.printf("%nO peso médio das pessoas é %.2f", mediaPeso);    }}