package org.example.entity;import java.util.Scanner;public class ConvercaoMoeda {    public static void comprarDolar() {        Scanner sc = new Scanner(System.in);        System.out.println("Qual a cotação do dólar?");        double cotacaoDolar = sc.nextDouble();        System.out.println("Quantos dólares quer comprar?");        double quntDolar = sc.nextDouble();        double valorBruto = cotacaoDolar * quntDolar;        double iof = valorBruto * 0.06;        double valorLiquido = valorBruto - iof;        System.out.printf("Valor Bruto comprado: $%.2f%n%nImposto recolhido: $%.2f%n%nValor " +                "Líquido comprado: $%.2f%n", valorBruto, iof, valorLiquido);        sc.close();    }}