package org.example.entity;import java.util.ArrayList;import java.util.Scanner;public class Funcionarios {    private final int id;    private String name;    private double salario;    private ArrayList<Funcionarios> func;    public Funcionarios() {        var sc = new Scanner(System.in);        System.out.println("Qual ID do funcion치rio? ");        id = sc.nextInt();        sc.nextLine();        System.out.println("Qual nome do funcion치rio? ");        name = sc.nextLine();        System.out.println("Qual o sal치rio do funcion치rio? ");        salario = sc.nextDouble();        sc.nextLine();    }    public int getId() {        return id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public double getSalario() {        return salario;    }    public void aumento(double e) {        salario += salario * (e / 100);    }}