package org.example.entity;import java.util.ArrayList;import java.util.Scanner;public class FuncionariosScript {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        var list = new ArrayList<Funcionarios>();        System.out.println("Quantos funcionários serão registrados? ");        int numFunc = sc.nextInt();        sc.nextLine();        for (int i = 0; i < numFunc; i++) {            System.out.printf("Funcionário #%d:%n", i + 1);            var func = new Funcionarios();            list.add(func);        }        System.out.println("Escolha um ID para dar aumento: ");        int escolha = sc.nextInt();        sc.nextLine();        boolean verificacao = !(list.stream().anyMatch(func -> func.getId() == escolha));        if (verificacao) {            while (verificacao) {                System.out.println("Esse ID não existe, escolha ou ID: ");                int reescolha = sc.nextInt();                sc.nextLine();                if(list.stream().anyMatch(func -> func.getId() == reescolha)){                    for (Funcionarios func : list){                        if(func.getId() == reescolha) {                            System.out.println("Quanto será o aumento? ");                            int e = sc.nextInt();                            sc.nextLine();                            func.aumento(e);                        }                    }                    System.out.println("Lista de funcionários: ");                    for (Funcionarios func : list) {                        System.out.printf("%d, %s, %.2f%n", func.getId(), func.getName(),func.getSalario());                    }                    verificacao = false;                }            }        } else {            for (Funcionarios func : list) {                if(func.getId() == escolha) {                    System.out.println("Quanto será o aumento? ");                    int e = sc.nextInt();                    sc.nextLine();                    func.aumento(e);                }            }            System.out.println("Lista de funcionários: ");            for (Funcionarios func : list) {                System.out.printf("%d, %s, %.2f%n", func.getId(), func.getName(),func.getSalario());            }        }        sc.close();    }}