package org.example.entity;public class Triangulo {    public double a, b, c, p, area;    public void Lados(double a, double b, double c) {        this.a = a;        this.b = b;        this.c = c;        this.p = (a + b + c) / 2;        Area(p);    }    public void Area(double p) {        this.area = Math.sqrt(p * (p - a) * (p - b) * (p - c));        System.out.printf("A área é: %.4f%n", area);    }}